import { Injectable } from '@angular/core';

@Injectable({
  providedIn: 'root',
})
export class ColorService {
  temp = 0;
  constructor() {}
  setTemp(t:number) {
    this.temp = t;
  }
  getColorByTemp(temp: number) {
    return temp < -10
      ? '#00c'
      : temp < 0
      ? '#0ae'
      : temp < 5
      ? '#0fe'
      : temp < 10
      ? '#0fd'
      : temp < 15
      ? '#0fc'
      : temp < 20
      ? '#0fa'
      : temp < 25
      ? '#0f5'
      : temp < 30
      ? '#ea0'
      : '#f00';
  }
}
