import { Component, OnDestroy, OnInit, ViewChild } from '@angular/core';
import { map, Observable, Subscription, tap } from 'rxjs';
import { IPlace, LANG, LAST_SEARCH } from 'src/app/constants';
import { GetCurrentPlaceService } from 'src/app/core/services/get-current-place.service';
import { HttpService } from 'src/app/core/services/http.service';
import { RememberPlacesService } from 'src/app/core/services/remember-places.service';
import { RequestService } from 'src/app/core/services/request.service';
import { SearchService } from 'src/app/core/services/search.service';
import { StorageService } from 'src/app/core/services/storage.service';
import { LastPlacesComponent } from './components/last-places/last-places.component';

@Component({
  selector: 'app-main',
  templateUrl: './main.component.html',
  styleUrls: ['./main.component.scss'],
})
export class MainComponent implements OnInit, OnDestroy {
  @ViewChild(LastPlacesComponent, { static: false })
  private lastPlaces: LastPlacesComponent | undefined;
  isMoreInfo=false;
  searchRequest = '';
  places$ = this.searchService.findPlaces();
  currentWeather$?: Observable<any>;
  isChosen = false;
  subscription1?: Subscription;
  subscription2?: Subscription;
  subscription3?: Subscription;
  constructor(
    private storage: StorageService,
    private searchService: SearchService,
    private requestService: RequestService,
    private http: HttpService,
    private rememberPlaces: RememberPlacesService,
    private getCurrentPlace: GetCurrentPlaceService
  ) {}

  ngOnInit(): void {
    this.searchRequest = this.storage.getItem(LAST_SEARCH) || '';
    this.subscription1 = this.requestService
      .getRequest()
      .subscribe((data: string) => {
        this.searchRequest = data;
        this.isChosen = false;
        this.rememberSearch();
      });
    this.subscription2 = this.getCurrentPlace
      .getPlace()
      .subscribe((data: any) => {
        this.getCurrentWeather(data.latitude, data.longitude);
        this.searchRequest = data.city;
        this.rememberSearch();
      });
    this.subscription3 = this.requestService
      .getCoords()
      .subscribe((data: [number | undefined, number | undefined]) => {
        this.getCurrentWeather(...data);
        this.isChosen = true;
      });
  }
  rememberSearch() {
    this.storage.setItem(LAST_SEARCH, this.searchRequest!);
  }
  findPlaces() {
    this.isChosen = false;
    this.requestService.setRequest(this.searchRequest);
  }
  getCurrentWeather(lat?: number, lon?: number) {
    this.isChosen = true;
    let currentLanguage = this.storage.getItem(LANG) || 'en';
    if (lat && lon) {
      this.currentWeather$ = this.http.getCurrentWeather(
        lat,
        lon,
        'metric',
        currentLanguage
      );
    }
  }
  getTime(time: number) {
    return new Date(time * 1000);
  }
  rememberPlace(place: IPlace) {
    this.rememberPlaces.remember(place);
  }
  convertPressure(p: number) {
    return (p * 0.750062).toFixed(0);
  }
  getColor(t:number){
    return t>0 ?  '#f00':'#4c6df0'
  }
  getFlagStyle(country:string){
   return `background-image: url(assets/img/png/${country.toLowerCase()}.png)`
  }
  ngOnDestroy() {
    this.subscription1?.unsubscribe();
    this.subscription2?.unsubscribe();
    this.subscription3?.unsubscribe();
  }
}
<div class="main-container">
  <div class="input-container">
    <input
      type="search"
      class="input-search"
      [placeholder]="'search place'"
      [(ngModel)]="searchRequest"
      (ngModelChange)="rememberSearch(); findPlaces()"
      autofocus
    />
  </div>

  <div class="places-container" *ngIf="isChosen == false">
    <div
      class="place-item"
      *ngFor="let place of places$ | async"
      (click)="getCurrentWeather(place.lat, place.lon); rememberPlace(place)"
    >
      <span>{{ place.name }}</span>
      <span> , </span>
      <span>{{ place.country }}</span>
      <span> , </span>
      <span>{{ place.state }}</span>
      <div class="flag-small" [style]="getFlagStyle(place.country)"></div>
    </div>
  </div>
  <div   class='loading'*ngIf="isChosen && !(currentWeather$ | async) as current"><p>LOADING...</p></div>
  <div
    class="forecast-container"
    [class.container_appear]="(isChosen && (currentWeather$ | async)) "
    (mouseenter)="isMoreInfo=true"
    (mouseleave)="isMoreInfo=false"
    [class.more]="isMoreInfo"
    
  >
    <p class="place">{{ current?.name }} , {{ current?.sys?.country }}</p>
    <div class="flag" [style]="getFlagStyle(current?.sys?.country)"></div>
    <p>{{ getTime(current?.dt) | date : "long" }}</p>
    <p>{{ current?.weather[0]?.main }}</p>
    <div class="current-temp">
      <p class="description">{{ current?.weather[0]?.description }}</p>
      <p class="temp" [style]="'color:'+getColor(current?.main?.temp)">
        {{ (current?.main?.temp).toFixed(1) }} ℃
      </p>
      <p class="feels-like">Feels like: {{ (current?.main?.feels_like).toFixed(1) }} ℃</p>
    </div>
    <p>
      min...max: {{ (current?.main?.temp_min).toFixed(1) }} ...
      {{ (current?.main?.temp_max).toFixed(1) }} ℃
    </p>
    <p>Pressure: {{ convertPressure(current?.main?.pressure) }} mmHG</p>
    <p>Humidity: {{ current?.main?.humidity }} %</p>
    <p>Cloudnees: {{ current?.clouds?.all }} %</p>
    <p>Wind: {{ current?.wind?.speed }} m/s</p>
  </div>
  <app-last-places class="last-places"></app-last-places>
</div>
